import org.junit.Test;
import static org.junit.Assert.assertEquals;

/**
 *
 * @author Michael <GrubenM@GMail.com>
 */
public class SolutionTest {
    @Test
    public void testExampleFromDescription() {
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("0000000011011010011100000110000001111110100111110011111100000000000111011111111011111011111000000101100011111100000111110011101100000100000")), "HEY JUDE");
    }

    @Test
    public void testShortMessages() {
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("")), "");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("0")), "");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("000000000000000000000000000000000000000000")), "");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("1")), "E");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("101")), "I");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("10001")), "EE");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("100001")), "EE");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("10000001")), "E E");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("100000001")), "E E");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("1000000001")), "E E");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("10000000001")), "E E");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("10111")), "A");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("1110111")), "M");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("111000111")), "I");
    }

    @Test
    public void testMultipleBitsPerDot() {
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("111")), "E");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("1111111")), "E");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("110011")), "I");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("111110000011111")), "I");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("11111100111111")), "M");
    }

    @Test
    public void testExtraZeros() {
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("01110")), "E");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("000000011100000")), "E");
    }

    @Test
    public void testEmptyMessage() {
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("")), "");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("0")), "");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("0000000000")), "");
    }

    @Test
    public void testLongMessage() {
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("1100110011001100000011000000111111001100111111001111110000000000000011001111110011111100111111000000110011001111110000001111110011001100000011")), "HEY JUDE");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("")), "THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG");
        assertEquals(Decoder.decodeMorse(Decoder.decodeBitsAdvanced("")), "SOS! THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG.");
    }
}
